        [Fact(Skip = "Integration test - requires network and QA API")]
        public async Task FetchMetarDatas_RealApi_ReturnsReports()
        {
            // Arrange
            string[] icaos = { "EKCH", "KJFK" };

            // Act
            var reports = await MetarFetchService.FetchMetarDatas(icaos);

            // Assert
            Xunit.Assert.NotEmpty(reports);
            Xunit.Assert.Contains(reports, r => r.conditions.ident == "EKCH");
            Xunit.Assert.Contains(reports, r => r.conditions.ident == "KJFK");
        }

